mapper.select:
SELECT RNUM , DONATE_NUMBER, DONATEWRITE_TITLE, USER_ID, USER_NAME, DONATE_AMOUNT, TO_CHAR(DONATE_DATE, 'YYYY.MM.DD') AS DONATE_DATE FROM( SELECT ROWNUM AS RNUM,D.DONATE_NUMBER, W.DONATEWRITE_TITLE, U.USER_ID, U.USER_NAME, D.DONATE_AMOUNT, DONATE_DATE FROM TBL_DONATE D, TBL_USERS U, TBL_DONATEWRITE W
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER AND U.USER_NAME LIKE '%' || #{searchVo.keyword} || '%' AND ROWNUM <= #{criteria.page} * #{criteria.amount}
ORDER BY D.DONATE_NUMBER
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER AND U.USER_ID LIKE '%' || #{searchVo.keyword} || '%' AND ROWNUM <= #{criteria.page} * #{criteria.amount}
ORDER BY D.DONATE_NUMBER
WHERE D.DONATEWRITE_NUMBER = W.DONATEWRITE_NUMBER AND U.USER_NUMBER = D.USER_NUMBER AND ROWNUM <= #{criteria.page} * #{criteria.amount}
ORDER BY D.DONATE_NUMBER
)X
WHERE USER_NAME LIKE '%' || #{searchVo.keyword} || '%' AND RNUM > (#{criteria.page} - 1) * #{criteria.amount} ORDER BY DONATE_NUMBER
WHERE USER_ID LIKE '%' || #{searchVo.keyword} || '%' AND RNUM > (#{criteria.page} - 1) * #{criteria.amount} ORDER BY DONATE_NUMBER
WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount} ORDER BY DONATE_NUMBER
