mapper.select:
SELECT RNUM,CSWRITE_NUMBER, ADMIN_NUMBER, CSWRITE_TITLE, TO_CHAR(TO_DATE(CSWRITE_RECRUIT_START, 'YYYY-MM-DD'), 'YYYY.MM.DD') AS CSWRITE_RECRUIT_START, TO_CHAR(TO_DATE(CSWRITE_RECRUIT_END, 'YYYY-MM-DD'), 'YYYY.MM.DD') AS CSWRITE_RECRUIT_END, CSWRITE_COUNT, CSWRITE_PLACE, TO_CHAR(TO_DATE(CSWRITE_ACT_START, 'YYYY-MM-DD'), 'YYYY.MM.DD') AS CSWRITE_ACT_START, TO_CHAR(TO_DATE(CSWRITE_ACT_END, 'YYYY-MM-DD'), 'YYYY.MM.DD') AS CSWRITE_ACT_END, TO_CHAR(CSWRITE_DATE, 'YYYY.MM.DD') AS CSWRITE_DATE, CSWRITE_MANAGER, CSWRITE_PHONE FROM( SELECT ROWNUM AS RNUM,CSWRITE_NUMBER, ADMIN_NUMBER, CSWRITE_TITLE, CSWRITE_RECRUIT_START, CSWRITE_RECRUIT_END, CSWRITE_COUNT, CSWRITE_PLACE, CSWRITE_ACT_START, CSWRITE_ACT_END, CSWRITE_DATE, CSWRITE_MANAGER, CSWRITE_PHONE FROM TBL_CSWRITE
WHERE CSWRITE_TITLE LIKE '%' || #{searchVo.keyword} || '%' AND ROWNUM <= #{criteria.page} * #{criteria.amount}
ORDER BY CSWRITE_NUMBER
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
ORDER BY CSWRITE_NUMBER
)X WHERE CSWRITE_TITLE LIKE '%' || #{searchVo.keyword} || '%' AND RNUM > (#{criteria.page} - 1) * #{criteria.amount}
